import Modal from '../UI/Modal';

import classes from './Cart.module.css';

const Cart = (props) => {

    const cartItems = <ul className={classes['cart-items']}> {
        [{ id: 'c1', name: 'sushi', amount: 2, price: 12.12 }].map((item) => {
            return <li key={item.id}>{item.name}</li>
        })
    } </ul>

    const closeCartHandler = () => {
        props.onCartOpen();
    }

    return (
        <Modal onCloseCart={closeCartHandler}>
            {cartItems}
            <div className={classes.total}>
                <span>Total Amount</span>
                <span>35.5</span>
            </div>
            <div className={classes.actions}>
                <button onClick={closeCartHandler} className={classes['button--alt']}>Close</button>
                <button onClick={closeCartHandler} className={classes.button}>Order</button>
            </div>
        </Modal>
    );
}

export default Cart;
